import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  // 상태 관리
  const [restaurants, setRestaurants] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [isAddDialogOpen, setIsAddDialogOpen] = useState(false);
  const [newRestaurant, setNewRestaurant] = useState({
    name: '',
    address: '',
    category: '',
    rating: '',
    distance: '',
    comment: '',
    link: '',
    sharedBy: ''
  });

  // 초기 데이터 로딩
  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('https://67866aa9f80b78923aa6bee6.mockapi.io/restaurants');
        const data = await response.json();
        setRestaurants(data);
      } catch (err) {
        setError('데이터를 가져오는 중 에러가 발생했습니다.');
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  // 맛집 추가 함수
  const addRestaurant = async () => {
    try {
      const response = await fetch('https://67866aa9f80b78923aa6bee6.mockapi.io/restaurants', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(newRestaurant),
      });
      const data = await response.json();
      setRestaurants([...restaurants, data]);
      setNewRestaurant({
        name: '',
        address: '',
        category: '',
        rating: '',
        distance: '',
        comment: '',
        link: '',
        sharedBy: ''
      });
      setIsAddDialogOpen(false);
    } catch (err) {
      console.error('추가 실패:', err);
    }
  };

  // 맛집 삭제 함수
  const deleteRestaurant = async (id) => {
    try {
      await fetch(`https://67866aa9f80b78923aa6bee6.mockapi.io/restaurants/${id}`, { method: 'DELETE' });
      setRestaurants(restaurants.filter((restaurant) => restaurant.id !== id));
    } catch (err) {
      console.error('삭제 실패:', err);
    }
  };

  // 카테고리 계산
  const uniqueCategories = [...new Set(restaurants.map((restaurant) => restaurant.category))];
  const categoryCount = uniqueCategories.length;

  if (loading) return <div>로딩 중...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div className="admin-dashboard">
      {/* 헤더 */}
      <header>
        <h1>숨슐랭 가이드</h1>
      </header>

      {/* 음식 카테고리 카드 */}
      <div className="stats-cards">
        <div className="card">
          <h2>음식 카테고리</h2>
          <p className="value">{categoryCount}</p>
        </div>
      </div>

      {/* 맛집 리스트 */}
      <section className="recent-restaurants">
        <div className="restaurants-header">
          <div>
            <h2>맛집 리스트</h2>
            <p>숨고 오피스 근처의 맛집을 공유해주세요!</p>
          </div>
          <button 
            className="add-restaurant-btn"
            onClick={() => setIsAddDialogOpen(true)}
          >
            맛집 추가하기
          </button>
        </div>
        <table>
          <thead>
            <tr>
              <th>이름</th>
              <th>주소</th>
              <th>카테고리</th>
              <th>별점</th>
              <th>거리</th>
              <th>코멘트</th>
              <th>링크</th>
              <th>공유자</th>
              <th>작업</th>
            </tr>
          </thead>
          <tbody>
            {restaurants.map((restaurant) => (
              <tr key={restaurant.id}>
                <td>{restaurant.name}</td>
                <td>{restaurant.address}</td>
                <td>{restaurant.category}</td>
                <td>{restaurant.rating}</td>
                <td>{restaurant.distance}</td>
                <td>{restaurant.comment}</td>
                <td>{restaurant.link}</td>
                <td>{restaurant.sharedBy}</td>
                <td>
                  <button className="delete-btn" onClick={() => deleteRestaurant(restaurant.id)}>삭제</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </section>

      {/* 커스텀 다이얼로그 */}
      {isAddDialogOpen && (
        <div className="dialog-overlay">
          <div className="dialog-content">
            <div className="dialog-header">
              <h2>새로운 맛집 추가하기</h2>
              <button 
                className="close-btn"
                onClick={() => setIsAddDialogOpen(false)}
              >
                ✕
              </button>
            </div>
            <div className="dialog-form">
              {[
                { label: '이름', key: 'name' },
                { label: '주소', key: 'address' },
                { label: '카테고리', key: 'category' },
                { label: '별점', key: 'rating' },
                { label: '거리', key: 'distance' },
                { label: '코멘트', key: 'comment' },
                { label: '링크', key: 'link' },
                { label: '공유자', key: 'sharedBy' }
              ].map((field) => (
                <div key={field.key} className="form-field">
                  <label>{field.label}</label>
                  <input
                    type="text"
                    value={newRestaurant[field.key]}
                    onChange={(e) => setNewRestaurant({
                      ...newRestaurant,
                      [field.key]: e.target.value
                    })}
                  />
                </div>
              ))}
              <button className="save-btn" onClick={addRestaurant}>
                저장하기
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
