import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [stats, setStats] = useState([]);
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const statsResponse = await fetch('https://67866aa9f80b78923aa6bee6.mockapi.io/stats');
        const statsData = await statsResponse.json();
        setStats(statsData);

        const ordersResponse = await fetch('https://67866aa9f80b78923aa6bee6.mockapi.io/orders');
        const ordersData = await ordersResponse.json();
        setOrders(ordersData);
      } catch (err) {
        setError('데이터를 가져오는 중 에러가 발생했습니다.');
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  if (loading) return <div>로딩 중...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div className="admin-dashboard">
      {/* 헤더 */}
      <header>
        <h1>관리자 대시보드</h1>
      </header>

      {/* 통계 카드 */}
      <div className="stats-cards">
        {stats.map((stat, index) => (
          <div className="card" key={index}>
            <h2>{stat.title}</h2>
            <p className="value">{stat.value}</p>
            <p className="change">{stat.change}</p>
          </div>
        ))}
      </div>

      {/* 최근 주문 */}
      <section className="recent-orders">
        <h2>최근 주문</h2>
        <p>최근에 들어온 주문 목록입니다</p>
        <table>
          <thead>
            <tr>
              <th>주문번호</th>
              <th>고객명</th>
              <th>상품</th>
              <th>금액</th>
              <th>상태</th>
            </tr>
          </thead>
          <tbody>
            {orders.map((order) => (
              <tr key={order.id}>
                <td>{order.id}</td>
                <td>{order.customerName}</td>
                <td>{order.product}</td>
                <td>{order.amount}</td>
                <td className={`status ${order.status}`}>{order.statusLabel}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </section>
    </div>
  );
}

export default App;
