import React, { useState, useEffect } from 'react';
// Search 아이콘 import 제거
import './App.css';

function App() {
  // 기본 상태 관리
  const [restaurants, setRestaurants] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [isAddDialogOpen, setIsAddDialogOpen] = useState(false);
  const [isEditDialogOpen, setIsEditDialogOpen] = useState(false);
  const [isCategoryDialogOpen, setIsCategoryDialogOpen] = useState(false);
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [editingRestaurant, setEditingRestaurant] = useState(null);
  const [showAlert, setShowAlert] = useState(false);
  const [alertMessage, setAlertMessage] = useState('');

  // 공유자 목록 (하드코딩)
  const SHARERS = ['김철수', '이영희', '박지성', '손흥민', '김연아'];

  const [newRestaurant, setNewRestaurant] = useState({
    name: '',
    address: '',
    category: '',
    rating: '',
    distance: '',
    comment: '',
    link: '',
    sharedBy: SHARERS[0]  // 기본값으로 첫 번째 공유자 설정
  });

  // 네이버 지도 API 검색 함수
  const searchNaverPlace = async (query) => {
    try {
      // 실제 구현시에는 네이버 지도 API 호출
      // 여기서는 예시로 더미 데이터 반환
      return {
        name: query,
        address: '서울시 강남구 테헤란로 123',
        category: '음식점 > 한식',
        rating: '4.5'
      };
    } catch (error) {
      console.error('네이버 지도 API 호출 실패:', error);
      throw error;
    }
  };

  // 데이터 가져오기
  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('https://67866aa9f80b78923aa6bee6.mockapi.io/restaurants');
        const data = await response.json();
        setRestaurants(data);
      } catch (err) {
        setError('데이터를 가져오는 중 에러가 발생했습니다.');
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  // 검색된 맛집 필터링
  const filteredRestaurants = restaurants.filter(restaurant => 
    restaurant.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
    restaurant.address.toLowerCase().includes(searchTerm.toLowerCase()) ||
    restaurant.category.toLowerCase().includes(searchTerm.toLowerCase())
  );

  // 중복 맛집 체크
  const isDuplicateRestaurant = (name) => {
    return restaurants.some(restaurant => 
      restaurant.name.toLowerCase() === name.toLowerCase()
    );
  };

  // 맛집 추가 함수
  const addRestaurant = async () => {
    try {
      // 중복 체크
      if (isDuplicateRestaurant(newRestaurant.name)) {
        setAlertMessage('이미 등록된 맛집입니다!');
        setShowAlert(true);
        setTimeout(() => setShowAlert(false), 3000);
        return;
      }

      const placeInfo = await searchNaverPlace(newRestaurant.name);

      const restaurantToAdd = {
        ...newRestaurant,
        address: placeInfo.address,
        category: placeInfo.category,
        rating: placeInfo.rating
      };

      const response = await fetch('https://67866aa9f80b78923aa6bee6.mockapi.io/restaurants', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(restaurantToAdd),
      });
      const data = await response.json();
      setRestaurants([...restaurants, data]);
      setNewRestaurant({
        name: '',
        address: '',
        category: '',
        rating: '',
        distance: '',
        comment: '',
        link: '',
        sharedBy: SHARERS[0]
      });
      setIsAddDialogOpen(false);
    } catch (err) {
      setAlertMessage('맛집 추가 중 오류가 발생했습니다.');
      setShowAlert(true);
      setTimeout(() => setShowAlert(false), 3000);
    }
  };

  // 카테고리별 통계 계산
  const getCategoryStats = () => {
    const stats = restaurants.reduce((acc, restaurant) => {
      const category = restaurant.category || '미분류';
      acc[category] = (acc[category] || 0) + 1;
      return acc;
    }, {});

    return Object.entries(stats).map(([category, count]) => ({
      category,
      count
    }));
  };

  // JSX 부분
  return (
    <div className="admin-dashboard">
      {/* Alert 메시지 */}
      {showAlert && (
        <div className="alert-message">
          {alertMessage}
        </div>
      )}

      {/* 헤더 섹션 */}
      <header className="dashboard-header">
        <h1>숨슐랭 가이드</h1>
        <p className="header-subtitle">맛있는 발견의 시작</p>
      </header>

      {/* 카테고리 통계 카드 섹션 */}
      <div className="stats-cards">
        {getCategoryStats().map(({ category, count }) => (
          <div 
            key={category} 
            className="stat-card"
            onClick={() => handleCategoryClick(category)}
          >
            <div className="stat-card-content">
              <h3 className="stat-card-title">{category}</h3>
              <p className="stat-card-value">{count}</p>
              <p className="stat-card-label">개의 맛집</p>
            </div>
          </div>
        ))}
      </div>

      {/* 맛집 리스트 섹션 */}
      <section className="restaurants-section">
        <div className="restaurants-header">
          <div className="header-content">
            <h2>맛집 리스트</h2>
            <p>숨고 오피스 근처의 맛집을 공유해주세요!</p>
          </div>
          <div className="header-actions">
            <div className="search-container">
              <span className="search-icon">🔍</span>
              <input
                type="text"
                placeholder="맛집 검색..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="search-input"
              />
            </div>
            <button 
              className="add-restaurant-btn"
              onClick={() => setIsAddDialogOpen(true)}
            >
              맛집 추가하기
            </button>
          </div>
        </div>

        {/* 테이블 컴포넌트 */}
        <div className="table-container">
          <table className="restaurants-table">
            <thead>
              <tr>
                <th>가게명</th>
                <th>도로명주소</th>
                <th>카테고리</th>
                <th>리뷰평점</th>
                <th>거리</th>
                <th>코멘트</th>
                <th>링크</th>
                <th>공유자</th>
                <th>작업</th>
              </tr>
            </thead>
            <tbody>
              {filteredRestaurants.map((restaurant) => (
                <tr key={restaurant.id}>
                  <td>{restaurant.name}</td>
                  <td>{restaurant.address}</td>
                  <td>{restaurant.category}</td>
                  <td>{restaurant.rating}</td>
                  <td>{restaurant.distance}</td>
                  <td>{restaurant.comment}</td>
                  <td>
                    {restaurant.link && (
                      <a 
                        href={restaurant.link}
                        target="_blank"
                        rel="noopener noreferrer"
                        className="restaurant-link"
                      >
                        바로가기
                      </a>
                    )}
                  </td>
                  <td>{restaurant.sharedBy}</td>
                  <td className="action-buttons">
                    <button 
                      className="edit-btn"
                      onClick={() => handleEdit(restaurant)}
                    >
                      수정
                    </button>
                    <button 
                      className="delete-btn"
                      onClick={() => deleteRestaurant(restaurant.id)}
                    >
                      삭제
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </section>

      {/* 맛집 추가 다이얼로그 */}
      {isAddDialogOpen && (
        <div className="dialog-overlay">
          <div className="dialog-content">
            <div className="dialog-header">
              <h2>새로운 맛집 추가하기</h2>
              <button 
                className="close-btn"
                onClick={() => setIsAddDialogOpen(false)}
              >
                ✕
              </button>
            </div>
            <div className="dialog-form">
              <div className="form-field">
                <label>가게명</label>
                <input
                  type="text"
                  value={newRestaurant.name}
                  onChange={(e) => setNewRestaurant({
                    ...newRestaurant,
                    name: e.target.value
                  })}
                />
              </div>
              <div className="form-field">
                <label>거리</label>
                <input
                  type="text"
                  value={newRestaurant.distance}
                  onChange={(e) => setNewRestaurant({
                    ...newRestaurant,
                    distance: e.target.value
                  })}
                />
              </div>
              <div className="form-field">
                <label>코멘트</label>
                <input
                  type="text"
                  value={newRestaurant.comment}
                  onChange={(e) => setNewRestaurant({
                    ...newRestaurant,
                    comment: e.target.value
                  })}
                />
              </div>
              <div className="form-field">
                <label>링크</label>
                <input
                  type="text"
                  value={newRestaurant.link}
                  onChange={(e) => setNewRestaurant({
                    ...newRestaurant,
                    link: e.target.value
                  })}
                />
              </div>
              <div className="form-field">
                <label>공유자</label>
                <select
                  value={newRestaurant.sharedBy}
                  onChange={(e) => setNewRestaurant({
                    ...newRestaurant,
                    sharedBy: e.target.value
                  })}
                >
                  {SHARERS.map(sharer => (
                    <option key={sharer} value={sharer}>
                      {sharer}
                    </option>
                  ))}
                </select>
              </div>
              <button className="save-btn" onClick={addRestaurant}>
                저장하기
              </button>
            </div>
          </div>
        </div>
      )}

      {/* 여기에 수정 다이얼로그와 카테고리 다이얼로그가 이어집니다 */}
    </div>
  );
}

export default App;
