// App.js
import React, { useState, useEffect } from 'react';
import './App.css';

// 공유자 목록 (하드코딩)
const SHARERS = ['김팀장', '이대리', '박사원', '최주임'];

// 네이버 지도 API 관련 상수
const NAVER_CLIENT_ID = 'YOUR_NAVER_CLIENT_ID';
const NAVER_CLIENT_SECRET = 'YOUR_NAVER_CLIENT_SECRET';

function App() {
  // 기본 상태 관리
  const [restaurants, setRestaurants] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [isAddDialogOpen, setIsAddDialogOpen] = useState(false);
  const [isEditDialogOpen, setIsEditDialogOpen] = useState(false);
  const [isCategoryDialogOpen, setIsCategoryDialogOpen] = useState(false);
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [editingRestaurant, setEditingRestaurant] = useState(null);
  const [searchQuery, setSearchQuery] = useState('');

  // 새로운 맛집 정보 상태
  const [newRestaurant, setNewRestaurant] = useState({
    name: '',
    address: '',
    category: '',
    rating: '',
    comment: '',
    link: '',
    sharedBy: SHARERS[0]
  });

  // 네이버 지도 API를 통한 장소 검색
  const searchNaverPlace = async (link) => {
    try {
      // 네이버 지도 링크에서 장소 ID 추출
      const placeId = extractPlaceIdFromLink(link);

      // 네이버 플레이스 API 호출
      const response = await fetch(`https://openapi.naver.com/v1/search/local.json?query=${placeId}`, {
        headers: {
          'X-Naver-Client-Id': NAVER_CLIENT_ID,
          'X-Naver-Client-Secret': NAVER_CLIENT_SECRET,
        }
      });

      const data = await response.json();
      const place = data.items[0];

      return {
        name: place.title.replace(/<[^>]*>/g, ''),
        address: place.roadAddress,
        category: place.category,
        rating: place.rating || '평가 없음'
      };
    } catch (err) {
      throw new Error('장소 정보를 가져오는데 실패했습니다.');
    }
  };

  // 중복 가게 확인
  const checkDuplicateRestaurant = (name) => {
    return restaurants.some(restaurant => 
      restaurant.name.toLowerCase() === name.toLowerCase()
    );
  };

  // 맛집 추가 함수
  const addRestaurant = async () => {
    try {
      // 네이버 지도 정보 가져오기
      const placeInfo = await searchNaverPlace(newRestaurant.link);

      // 중복 체크
      if (checkDuplicateRestaurant(placeInfo.name)) {
        alert(`'${placeInfo.name}'은(는) 이미 등록된 맛집입니다.`);
        return;
      }

      const restaurantData = {
        ...placeInfo,
        comment: newRestaurant.comment,
        link: newRestaurant.link,
        sharedBy: newRestaurant.sharedBy
      };

      const response = await fetch('https://67866aa9f80b78923aa6bee6.mockapi.io/restaurants', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(restaurantData),
      });

      const data = await response.json();
      setRestaurants([...restaurants, data]);
      setNewRestaurant({
        name: '',
        address: '',
        category: '',
        rating: '',
        comment: '',
        link: '',
        sharedBy: SHARERS[0]
      });
      setIsAddDialogOpen(false);
    } catch (err) {
      alert('맛집 추가 중 오류가 발생했습니다: ' + err.message);
    }
  };

  // 검색 필터링된 맛집 목록
  const filteredRestaurants = restaurants.filter(restaurant =>
    restaurant.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
    restaurant.category.toLowerCase().includes(searchQuery.toLowerCase()) ||
    restaurant.address.toLowerCase().includes(searchQuery.toLowerCase())
  );

  return (
    <div className="admin-dashboard">
      <header className="dashboard-header">
        <h1>숨슐랭 가이드</h1>
        <p className="header-subtitle">맛있는 발견의 시작</p>
      </header>

      <div className="stats-cards">
        {getCategoryStats().map(({ category, count }) => (
          <div 
            key={category} 
            className="stat-card"
            onClick={() => handleCategoryClick(category)}
          >
            <div className="stat-card-content">
              <h3 className="stat-card-title">{category}</h3>
              <p className="stat-card-value">{count}</p>
              <p className="stat-card-label">개의 맛집</p>
            </div>
          </div>
        ))}
      </div>

      <section className="restaurants-section">
        <div className="restaurants-header">
          <div className="header-content">
            <h2>맛집 리스트</h2>
            <p>숨고 오피스 근처의 맛집을 공유해주세요!</p>
          </div>
          <div className="header-actions">
            <div className="search-container">
              <input
                type="text"
                placeholder="맛집 검색..."
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                className="search-input"
              />
            </div>
            <button 
              className="add-restaurant-btn"
              onClick={() => setIsAddDialogOpen(true)}
            >
              맛집 추가하기
            </button>
          </div>
        </div>

        <div className="table-container">
          <table className="restaurants-table">
            <thead>
              <tr>
                <th>가게명</th>
                <th>도로명주소</th>
                <th>카테고리</th>
                <th>리뷰평점</th>
                <th>코멘트</th>
                <th>링크</th>
                <th>공유자</th>
                <th>작업</th>
              </tr>
            </thead>
            <tbody>
              {filteredRestaurants.map((restaurant) => (
                <tr key={restaurant.id}>
                  <td>{restaurant.name}</td>
                  <td>{restaurant.address}</td>
                  <td>{restaurant.category}</td>
                  <td>{restaurant.rating}</td>
                  <td>{restaurant.comment}</td>
                  <td>
                    {restaurant.link && (
                      <a 
                        href={restaurant.link}
                        target="_blank"
                        rel="noopener noreferrer"
                        className="restaurant-link"
                      >
                        바로가기
                      </a>
                    )}
                  </td>
                  <td>{restaurant.sharedBy}</td>
                  <td className="action-buttons">
                    <button 
                      className="edit-btn"
                      onClick={() => handleEdit(restaurant)}
                    >
                      수정
                    </button>
                    <button 
                      className="delete-btn"
                      onClick={() => deleteRestaurant(restaurant.id)}
                    >
                      삭제
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </section>

      {/* 맛집 추가 다이얼로그 */}
      {isAddDialogOpen && (
        <div className="dialog-overlay" onClick={() => setIsAddDialogOpen(false)}>
          <div className="dialog-content" onClick={e => e.stopPropagation()}>
            <div className="dialog-header">
              <h2>새로운 맛집 추가하기</h2>
              <button 
                className="close-btn"
                onClick={() => setIsAddDialogOpen(false)}
              >
                ✕
              </button>
            </div>
            <div className="dialog-form">
              <div className="form-field">
                <label>공유자</label>
                <select
                  value={newRestaurant.sharedBy}
                  onChange={(e) => setNewRestaurant({
                    ...newRestaurant,
                    sharedBy: e.target.value
                  })}
                  className="form-select"
                >
                  {SHARERS.map(sharer => (
                    <option key={sharer} value={sharer}>{sharer}</option>
                  ))}
                </select>
              </div>
              <div className="form-field">
                <label>네이버 지도 링크</label>
                <input
                  type="text"
                  value={newRestaurant.link}
                  onChange={(e) => setNewRestaurant({
                    ...newRestaurant,
                    link: e.target.value
                  })}
                  placeholder="네이버 지도 URL을 입력해주세요"
                />
              </div>
              <div className="form-field">
                <label>코멘트</label>
                <textarea
                  value={newRestaurant.comment}
                  onChange={(e) => setNewRestaurant({
                    ...newRestaurant,
                    comment: e.target.value
                  })}
                  placeholder="맛집에 대한 코멘트를 입력해주세요"
                  className="form-textarea"
                />
              </div>
              <button className="save-btn" onClick={addRestaurant}>
                저장하기
              </button>
            </div>
          </div>
        </div>
      )}

      {/* 나머지 다이얼로그 컴포넌트들... */}
    </div>
  );
}

export default App;
