import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [restaurants, setRestaurants] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [newRestaurant, setNewRestaurant] = useState({
    name: '',
    address: '',
    category: '',
    rating: '',
    distance: '',
    comment: '',
    link: '',
    sharedBy: ''
  });
  const [editingRestaurant, setEditingRestaurant] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('https://67866aa9f80b78923aa6bee6.mockapi.io/restaurants');
        const data = await response.json();
        setRestaurants(data);
      } catch (err) {
        setError('데이터를 가져오는 중 에러가 발생했습니다.');
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  // 맛집 추가 함수
  const addRestaurant = async () => {
    try {
      const response = await fetch('https://67866aa9f80b78923aa6bee6.mockapi.io/restaurants', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(newRestaurant),
      });
      const data = await response.json();
      setRestaurants([...restaurants, data]);
      setNewRestaurant({ name: '', address: '', category: '', rating: '', distance: '', comment: '', link: '', sharedBy: '' });
    } catch (err) {
      console.error('추가 실패:', err);
    }
  };

  // 맛집 삭제 함수
  const deleteRestaurant = async (id) => {
    try {
      await fetch(`https://67866aa9f80b78923aa6bee6.mockapi.io/restaurants/${id}`, { method: 'DELETE' });
      setRestaurants(restaurants.filter((restaurant) => restaurant.id !== id));
    } catch (err) {
      console.error('삭제 실패:', err);
    }
  };

  // 맛집 수정 함수
  const updateRestaurant = async () => {
    try {
      const response = await fetch(`https://67866aa9f80b78923aa6bee6.mockapi.io/restaurants/${editingRestaurant.id}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(editingRestaurant),
      });
      const updatedRestaurant = await response.json();
      setRestaurants(restaurants.map((restaurant) => (restaurant.id === updatedRestaurant.id ? updatedRestaurant : restaurant)));
      setEditingRestaurant(null);
    } catch (err) {
      console.error('수정 실패:', err);
    }
  };

  // 음식 카테고리 별로 고유한 음식점 수 구하기
  const uniqueCategories = [...new Set(restaurants.map((restaurant) => restaurant.category))];
  const categoryCount = uniqueCategories.length;

  if (loading) return <div>로딩 중...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div className="admin-dashboard">
      {/* 헤더 */}
      <header>
        <h1>숨슐랭 가이드</h1>
      </header>

      {/* 음식 카테고리 카드 */}
      <div className="stats-cards">
        <div className="card">
          <h2>음식 카테고리</h2>
          <p className="value">{categoryCount}</p>
        </div>
      </div>

      {/* 맛집 리스트 */}
      <section className="recent-restaurants">
        <h2>맛집 리스트</h2>
        <p>숨고 오피스 근처의 맛집을 공유해주세요!</p>
        <button className="add-restaurant-btn" onClick={() => setEditingRestaurant({})}>맛집 추가하기</button>
        <table>
          <thead>
            <tr>
              <th>이름</th>
              <th>주소</th>
              <th>카테고리</th>
              <th>별점</th>
              <th>거리</th>
              <th>코멘트</th>
              <th>링크</th>
              <th>공유자</th>
              <th>작업</th>
            </tr>
          </thead>
          <tbody>
            {restaurants.map((restaurant) => (
              <tr key={restaurant.id}>
                <td>{restaurant.name}</td>
                <td>{restaurant.address}</td>
                <td>{restaurant.category}</td>
                <td>{restaurant.rating}</td>
                <td>{restaurant.distance}</td>
                <td>{restaurant.comment}</td>
                <td>{restaurant.link}</td>
                <td>{restaurant.sharedBy}</td>
                <td>
                  <button onClick={() => setEditingRestaurant(restaurant)}>수정</button>
                  <button onClick={() => deleteRestaurant(restaurant.id)}>삭제</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </section>

      {/* 수정 UI */}
      {editingRestaurant && (
        <section>
          <h2>맛집 수정</h2>
          <input
            type="text"
            placeholder="이름"
            value={editingRestaurant.name}
            onChange={(e) => setEditingRestaurant({ ...editingRestaurant, name: e.target.value })}
          />
          <input
            type="text"
            placeholder="주소"
            value={editingRestaurant.address}
            onChange={(e) => setEditingRestaurant({ ...editingRestaurant, address: e.target.value })}
          />
          <input
            type="text"
            placeholder="카테고리"
            value={editingRestaurant.category}
            onChange={(e) => setEditingRestaurant({ ...editingRestaurant, category: e.target.value })}
          />
          <input
            type="text"
            placeholder="별점"
            value={editingRestaurant.rating}
            onChange={(e) => setEditingRestaurant({ ...editingRestaurant, rating: e.target.value })}
          />
          <input
            type="text"
            placeholder="거리"
            value={editingRestaurant.distance}
            onChange={(e) => setEditingRestaurant({ ...editingRestaurant, distance: e.target.value })}
          />
          <input
            type="text"
            placeholder="코멘트"
            value={editingRestaurant.comment}
            onChange={(e) => setEditingRestaurant({ ...editingRestaurant, comment: e.target.value })}
          />
          <input
            type="text"
            placeholder="링크"
            value={editingRestaurant.link}
            onChange={(e) => setEditingRestaurant({ ...editingRestaurant, link: e.target.value })}
          />
          <input
            type="text"
            placeholder="공유자"
            value={editingRestaurant.sharedBy}
            onChange={(e) => setEditingRestaurant({ ...editingRestaurant, sharedBy: e.target.value })}
          />
          <button onClick={updateRestaurant}>수정 완료</button>
          <button onClick={() => setEditingRestaurant(null)}>취소</button>
        </section>
      )}
    </div>
  );
}

export default App;
