import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  // 기본 상태 관리
  const [restaurants, setRestaurants] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [isAddDialogOpen, setIsAddDialogOpen] = useState(false);
  const [isEditDialogOpen, setIsEditDialogOpen] = useState(false);
  const [isCategoryDialogOpen, setIsCategoryDialogOpen] = useState(false);
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [editingRestaurant, setEditingRestaurant] = useState(null);
  const [searchKeyword, setSearchKeyword] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [newRestaurant, setNewRestaurant] = useState({
    name: '',
    address: '',
    category: '',
    rating: '',
    distance: '',
    comment: '',
    link: '',
    sharedBy: ''
  });

  // 공유자 목록 (하드코딩)
  const SHARERS = [
    '김서준', '이민지', '박현우', '정수빈', '최예준',
    '강지원', '윤서연', '임도현', '한미래', '신준호'
  ];

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('https://67866aa9f80b78923aa6bee6.mockapi.io/restaurants');
        const data = await response.json();
        setRestaurants(data);
      } catch (err) {
        setError('데이터를 가져오는 중 에러가 발생했습니다.');
      } finally {
        setLoading(false);
      }
    };

    // 네이버 지도 API 초기화
    const initNaverMap = () => {
      const script = document.createElement('script');
      script.src = `https://openapi.map.naver.com/openapi/v3/maps.js?ncpClientId=YOUR_CLIENT_ID&submodules=geocoder`;
      script.async = true;
      document.head.appendChild(script);
    };

    fetchData();
    initNaverMap();
  }, []);

  // 네이버 장소 검색
  const searchPlace = async (keyword) => {
    try {
      // 실제 구현시에는 서버를 통해 네이버 검색 API를 호출해야 합니다
      const response = await fetch(`/api/search/place?query=${encodeURIComponent(keyword)}`);
      const data = await response.json();
      setSearchResults(data.places);
    } catch (err) {
      console.error('장소 검색 실패:', err);
    }
  };

  // 검색 결과에서 장소 선택
  const selectPlace = (place) => {
    setNewRestaurant({
      ...newRestaurant,
      name: place.name,
      address: place.address,
      category: place.category,
      rating: place.rating || '',
      link: `https://map.naver.com/v5/entry/place/${place.id}`,
    });
    setSearchResults([]);
  };

  // 카테고리별 통계 계산
  const getCategoryStats = () => {
    const stats = restaurants.reduce((acc, restaurant) => {
      const category = restaurant.category || '미분류';
      acc[category] = (acc[category] || 0) + 1;
      return acc;
    }, {});

    return Object.entries(stats).map(([category, count]) => ({
      category,
      count
    }));
  };

  // API 관련 함수들은 동일하게 유지...

  const categoryStats = getCategoryStats();

  return (
    <div className="admin-dashboard">
      {/* 헤더 섹션은 동일 */}

      {/* 카테고리 통계 카드 섹션 */}
      <div className="stats-cards">
        {categoryStats.map(({ category, count }) => (
          <div 
            key={category} 
            className="stat-card"
            onClick={() => handleCategoryClick(category)}
          >
            <div className="stat-card-content">
              <p className="stat-card-value">{count}</p>
              <h3 className="stat-card-title">{category}</h3>
              <p className="stat-card-label">개의 맛집</p>
            </div>
          </div>
        ))}
      </div>

      {/* 맛집 추가 다이얼로그 */}
      {isAddDialogOpen && (
        <div className="dialog-overlay" onClick={() => setIsAddDialogOpen(false)}>
          <div className="dialog-content" onClick={e => e.stopPropagation()}>
            <div className="dialog-header">
              <h2>새로운 맛집 추가하기</h2>
              <button className="close-btn" onClick={() => setIsAddDialogOpen(false)}>✕</button>
            </div>
            <div className="dialog-form">
              {/* 장소 검색 필드 */}
              <div className="form-field search-field">
                <label>장소 검색</label>
                <div className="search-input-wrapper">
                  <input
                    type="text"
                    value={searchKeyword}
                    onChange={(e) => {
                      setSearchKeyword(e.target.value);
                      if (e.target.value.length >= 2) {
                        searchPlace(e.target.value);
                      }
                    }}
                    placeholder="가게 이름을 검색하세요"
                  />
                  {searchResults.length > 0 && (
                    <div className="search-results">
                      {searchResults.map((place) => (
                        <div
                          key={place.id}
                          className="search-result-item"
                          onClick={() => selectPlace(place)}
                        >
                          <h4>{place.name}</h4>
                          <p>{place.address}</p>
                        </div>
                      ))}
                    </div>
                  )}
                </div>
              </div>

              {/* 자동 입력 필드들 */}
              <div className="form-field">
                <label>가게명</label>
                <input type="text" value={newRestaurant.name} readOnly />
              </div>
              <div className="form-field">
                <label>주소</label>
                <input type="text" value={newRestaurant.address} readOnly />
              </div>
              <div className="form-field">
                <label>카테고리</label>
                <input type="text" value={newRestaurant.category} readOnly />
              </div>
              <div className="form-field">
                <label>별점</label>
                <input type="text" value={newRestaurant.rating} readOnly />
              </div>

              {/* 수동 입력 필드들 */}
              <div className="form-field">
                <label>거리 (도보 예상시간)</label>
                <input
                  type="text"
                  value={newRestaurant.distance}
                  onChange={(e) => setNewRestaurant({...newRestaurant, distance: e.target.value})}
                  placeholder="예: 도보 5분"
                />
              </div>
              <div className="form-field">
                <label>코멘트</label>
                <textarea
                  value={newRestaurant.comment}
                  onChange={(e) => setNewRestaurant({...newRestaurant, comment: e.target.value})}
                  placeholder="맛집에 대한 코멘트를 입력하세요"
                />
              </div>

              {/* 공유자 선택 드롭다운 */}
              <div className="form-field">
                <label>공유자</label>
                <select
                  value={newRestaurant.sharedBy}
                  onChange={(e) => setNewRestaurant({...newRestaurant, sharedBy: e.target.value})}
                >
                  <option value="">공유자를 선택하세요</option>
                  {SHARERS.map(sharer => (
                    <option key={sharer} value={sharer}>{sharer}</option>
                  ))}
                </select>
              </div>

              <button className="save-btn" onClick={addRestaurant}>
                저장하기
              </button>
            </div>
          </div>
        </div>
      )}

      {/* 나머지 다이얼로그들은 유사한 방식으로 수정... */}
    </div>
  );
}

export default App;
