import React, { useState, useEffect } from 'react';
import './App.css';

// 상수 정의
const SHARERS = ['김팀장', '이대리', '박사원', '최주임'];
const API_BASE_URL = 'https://67866aa9f80b78923aa6bee6.mockapi.io/restaurants';

function App() {
  // 상태 관리
  const [restaurants, setRestaurants] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchQuery, setSearchQuery] = useState('');

  // 모달 상태 관리
  const [isAddDialogOpen, setIsAddDialogOpen] = useState(false);
  const [isEditDialogOpen, setIsEditDialogOpen] = useState(false);
  const [isCategoryDialogOpen, setIsCategoryDialogOpen] = useState(false);
  const [selectedCategory, setSelectedCategory] = useState(null);

  // 폼 데이터 관리
  const [editingRestaurant, setEditingRestaurant] = useState(null);
  const [newRestaurant, setNewRestaurant] = useState({
    name: '',
    address: '',
    category: '',
    rating: '',
    comment: '',
    link: '',
    sharedBy: SHARERS[0]
  });

  // 데이터 가져오기
  useEffect(() => {
    fetchRestaurants();
  }, []);

  // API 요청 함수들
  const fetchRestaurants = async () => {
    try {
      setLoading(true);
      const response = await fetch(API_BASE_URL);
      if (!response.ok) throw new Error('데이터를 불러오는데 실패했습니다');
      const data = await response.json();
      setRestaurants(data);
    } catch (err) {
      setError(err.message);
      console.error('데이터 로딩 에러:', err);
    } finally {
      setLoading(false);
    }
  };

  // 네이버 지도 URL 검증
  const validateNaverMapUrl = (url) => {
    try {
      const parsedUrl = new URL(url);
      return parsedUrl.hostname.includes('naver.com') && parsedUrl.pathname.includes('map');
    } catch {
      return false;
    }
  };

  // 중복 체크
  const checkDuplicateRestaurant = (name) => {
    return restaurants.some(restaurant => 
      restaurant.name.toLowerCase().trim() === name.toLowerCase().trim()
    );
  };

  // 맛집 추가
  const handleAddRestaurant = async () => {
    try {
      // 입력 검증
      if (!newRestaurant.link) {
        alert('네이버 지도 링크를 입력해주세요');
        return;
      }

      if (!validateNaverMapUrl(newRestaurant.link)) {
        alert('올바른 네이버 지도 링크를 입력해주세요');
        return;
      }

      if (!newRestaurant.sharedBy) {
        alert('공유자를 선택해주세요');
        return;
      }

      const response = await fetch(API_BASE_URL, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(newRestaurant)
      });

      if (!response.ok) throw new Error('맛집 추가에 실패했습니다');

      const addedRestaurant = await response.json();
      setRestaurants(prev => [...prev, addedRestaurant]);
      setIsAddDialogOpen(false);
      resetNewRestaurant();
    } catch (err) {
      alert(err.message);
    }
  };

  // 맛집 수정
  const handleUpdateRestaurant = async () => {
    try {
      if (!editingRestaurant?.id) return;

      const response = await fetch(`${API_BASE_URL}/${editingRestaurant.id}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(editingRestaurant)
      });

      if (!response.ok) throw new Error('맛집 수정에 실패했습니다');

      const updatedRestaurant = await response.json();
      setRestaurants(prev => 
        prev.map(rest => rest.id === updatedRestaurant.id ? updatedRestaurant : rest)
      );
      setIsEditDialogOpen(false);
      setEditingRestaurant(null);
    } catch (err) {
      alert(err.message);
    }
  };

  // 맛집 삭제
  const handleDeleteRestaurant = async (id) => {
    if (!window.confirm('정말 삭제하시겠습니까?')) return;

    try {
      const response = await fetch(`${API_BASE_URL}/${id}`, {
        method: 'DELETE'
      });

      if (!response.ok) throw new Error('삭제에 실패했습니다');

      setRestaurants(prev => prev.filter(rest => rest.id !== id));
    } catch (err) {
      alert(err.message);
    }
  };

  // 유틸리티 함수들
  const resetNewRestaurant = () => {
    setNewRestaurant({
      name: '',
      address: '',
      category: '',
      rating: '',
      comment: '',
      link: '',
      sharedBy: SHARERS[0]
    });
  };

  const getCategoryStats = () => {
    const stats = restaurants.reduce((acc, restaurant) => {
      const category = restaurant.category || '미분류';
      acc[category] = (acc[category] || 0) + 1;
      return acc;
    }, {});

    return Object.entries(stats).map(([category, count]) => ({
      category,
      count
    }));
  };

  // 검색 및 필터링
  const filteredRestaurants = restaurants.filter(restaurant => {
    const query = searchQuery.toLowerCase();
    return (
      restaurant.name?.toLowerCase().includes(query) ||
      restaurant.category?.toLowerCase().includes(query) ||
      restaurant.address?.toLowerCase().includes(query)
    );
  });

  // 로딩 상태
  if (loading) {
    return <div className="loading">데이터를 불러오는 중...</div>;
  }

  // 에러 상태
  if (error) {
    return <div className="error">에러가 발생했습니다: {error}</div>;
  }

  return (
    <div className="admin-dashboard">
      <header className="dashboard-header">
        <h1>숨슐랭 가이드</h1>
        <p className="header-subtitle">맛있는 발견의 시작</p>
      </header>

      <div className="stats-cards">
        {getCategoryStats().map(({ category, count }) => (
          <div 
            key={category} 
            className="stat-card"
            onClick={() => setSelectedCategory(category)}
          >
            <div className="stat-card-content">
              <h3 className="stat-card-title">{category}</h3>
              <p className="stat-card-value">{count}</p>
              <p className="stat-card-label">개의 맛집</p>
            </div>
          </div>
        ))}
      </div>

      <section className="restaurants-section">
        <div className="restaurants-header">
          <div className="header-content">
            <h2>맛집 리스트</h2>
            <p>숨고 오피스 근처의 맛집을 공유해주세요!</p>
          </div>
          <div className="header-actions">
            <div className="search-container">
              <input
                type="text"
                placeholder="맛집 검색..."
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                className="search-input"
              />
            </div>
            <button 
              className="add-restaurant-btn"
              onClick={() => setIsAddDialogOpen(true)}
            >
              맛집 추가하기
            </button>
          </div>
        </div>

        <div className="table-container">
          <table className="restaurants-table">
            <thead>
              <tr>
                <th>가게명</th>
                <th>도로명주소</th>
                <th>카테고리</th>
                <th>리뷰평점</th>
                <th>코멘트</th>
                <th>링크</th>
                <th>공유자</th>
                <th>작업</th>
              </tr>
            </thead>
            <tbody>
              {filteredRestaurants.map((restaurant) => (
                <tr key={restaurant.id}>
                  <td>{restaurant.name}</td>
                  <td>{restaurant.address}</td>
                  <td>{restaurant.category}</td>
                  <td>{restaurant.rating}</td>
                  <td>{restaurant.comment}</td>
                  <td>
                    {restaurant.link && (
                      <a 
                        href={restaurant.link}
                        target="_blank"
                        rel="noopener noreferrer"
                        className="restaurant-link"
                      >
                        바로가기
                      </a>
                    )}
                  </td>
                  <td>{restaurant.sharedBy}</td>
                  <td className="action-buttons">
                    <button 
                      className="edit-btn"
                      onClick={() => {
                        setEditingRestaurant({...restaurant});
                        setIsEditDialogOpen(true);
                      }}
                    >
                      수정
                    </button>
                    <button 
                      className="delete-btn"
                      onClick={() => handleDeleteRestaurant(restaurant.id)}
                    >
                      삭제
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </section>

      {/* 맛집 추가 다이얼로그 */}
      {isAddDialogOpen && (
        <div className="dialog-overlay" onClick={() => setIsAddDialogOpen(false)}>
          <div className="dialog-content" onClick={e => e.stopPropagation()}>
            <div className="dialog-header">
              <h2>새로운 맛집 추가하기</h2>
              <button 
                className="close-btn"
                onClick={() => setIsAddDialogOpen(false)}
              >
                ✕
              </button>
            </div>
            <div className="dialog-form">
              <div className="form-field">
                <label>공유자</label>
                <select
                  value={newRestaurant.sharedBy}
                  onChange={(e) => setNewRestaurant(prev => ({
                    ...prev,
                    sharedBy: e.target.value
                  }))}
                  className="form-select"
                >
                  {SHARERS.map(sharer => (
                    <option key={sharer} value={sharer}>{sharer}</option>
                  ))}
                </select>
              </div>
              <div className="form-field">
                <label>네이버 지도 링크</label>
                <input
                  type="text"
                  value={newRestaurant.link}
                  onChange={(e) => setNewRestaurant(prev => ({
                    ...prev,
                    link: e.target.value
                  }))}
                  placeholder="네이버 지도 URL을 입력해주세요"
                />
              </div>
              <div className="form-field">
                <label>코멘트</label>
                <textarea
                  value={newRestaurant.comment}
                  onChange={(e) => setNewRestaurant(prev => ({
                    ...prev,
                    comment: e.target.value
                  }))}
                  placeholder="맛집에 대한 코멘트를 입력해주세요"
                  className="form-textarea"
                />
              </div>
              <button 
                className="save-btn"
                onClick={handleAddRestaurant}
              >
                저장하기
              </button>
            </div>
          </div>
        </div>
      )}

      {/* 맛집 수정 다이얼로그 */}
      {isEditDialogOpen && editingRestaurant && (
        <div className="dialog-overlay" onClick={() => setIsEditDialogOpen(false)}>
          <div className="dialog-content" onClick={e => e.stopPropagation()}>
            <div className="dialog-header">
              <h2>맛집 정보 수정하기</h2>
              <button 
                className="close-btn"
                onClick={() => setIsEditDialogOpen(false)}
              >
                ✕
              </button>
            </div>
            <div className="dialog-form">
              <div className="form-field">
                <label>코멘트</label>
                <textarea
                  value={editingRestaurant.comment}
                  onChange={(e) => setEditingRestaurant(prev => ({
                    ...prev,
                    comment: e.target.value
                  }))}
                  className="form-textarea"
                />
              </div>
              <button 
                className="save-btn"
                onClick={handleUpdateRestaurant}
              >
                수정 완료
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
