import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  // 기본 상태 관리
  const [restaurants, setRestaurants] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [isAddDialogOpen, setIsAddDialogOpen] = useState(false);
  const [isEditDialogOpen, setIsEditDialogOpen] = useState(false);
  const [isCategoryDialogOpen, setIsCategoryDialogOpen] = useState(false);
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [editingRestaurant, setEditingRestaurant] = useState(null);
  const [newRestaurant, setNewRestaurant] = useState({
    name: '',
    address: '',
    category: '',
    rating: '',
    distance: '',
    comment: '',
    link: '',
    sharedBy: ''
  });

  // 데이터 가져오기
  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('https://67866aa9f80b78923aa6bee6.mockapi.io/restaurants');
        const data = await response.json();
        setRestaurants(data);
      } catch (err) {
        setError('데이터를 가져오는 중 에러가 발생했습니다.');
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  // 카테고리별 통계 계산
  const getCategoryStats = () => {
    const stats = restaurants.reduce((acc, restaurant) => {
      const category = restaurant.category || '미분류';
      acc[category] = (acc[category] || 0) + 1;
      return acc;
    }, {});

    return Object.entries(stats).map(([category, count]) => ({
      category,
      count
    }));
  };

  // 카테고리별 음식점 필터링
  const getRestaurantsByCategory = (category) => {
    return restaurants.filter(restaurant => restaurant.category === category);
  };

  // 맛집 추가 함수
  const addRestaurant = async () => {
    try {
      const response = await fetch('https://67866aa9f80b78923aa6bee6.mockapi.io/restaurants', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(newRestaurant),
      });
      const data = await response.json();
      setRestaurants([...restaurants, data]);
      setNewRestaurant({
        name: '',
        address: '',
        category: '',
        rating: '',
        distance: '',
        comment: '',
        link: '',
        sharedBy: ''
      });
      setIsAddDialogOpen(false);
    } catch (err) {
      console.error('추가 실패:', err);
    }
  };

  // 맛집 수정 함수
  const updateRestaurant = async () => {
    try {
      const response = await fetch(`https://67866aa9f80b78923aa6bee6.mockapi.io/restaurants/${editingRestaurant.id}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(editingRestaurant),
      });
      const updatedRestaurant = await response.json();
      setRestaurants(restaurants.map((restaurant) => 
        restaurant.id === updatedRestaurant.id ? updatedRestaurant : restaurant
      ));
      setEditingRestaurant(null);
      setIsEditDialogOpen(false);
    } catch (err) {
      console.error('수정 실패:', err);
    }
  };

  // 맛집 삭제 함수
  const deleteRestaurant = async (id) => {
    if (window.confirm('정말 삭제하시겠습니까?')) {
      try {
        await fetch(`https://67866aa9f80b78923aa6bee6.mockapi.io/restaurants/${id}`, { 
          method: 'DELETE' 
        });
        setRestaurants(restaurants.filter((restaurant) => restaurant.id !== id));
      } catch (err) {
        console.error('삭제 실패:', err);
      }
    }
  };

  const handleCategoryClick = (category) => {
    setSelectedCategory(category);
    setIsCategoryDialogOpen(true);
  };

  const handleEdit = (restaurant) => {
    setEditingRestaurant({...restaurant});
    setIsEditDialogOpen(true);
  };

  if (loading) return <div className="loading">로딩 중...</div>;
  if (error) return <div className="error">{error}</div>;

  const categoryStats = getCategoryStats();

  return (
    <div className="admin-dashboard">
      {/* 헤더 섹션 */}
      <header className="dashboard-header">
        <h1>숨슐랭 가이드</h1>
        <p className="header-subtitle">맛있는 발견의 시작</p>
      </header>

      {/* 카테고리 통계 카드 섹션 */}
      <div className="stats-cards">
        {categoryStats.map(({ category, count }) => (
          <div 
            key={category} 
            className="stat-card"
            onClick={() => handleCategoryClick(category)}
          >
            <div className="stat-card-content">
              <h3 className="stat-card-title">{category}</h3>
              <p className="stat-card-value">{count}</p>
              <p className="stat-card-label">등록된 맛집</p>
            </div>
          </div>
        ))}
      </div>

      {/* 맛집 리스트 섹션 */}
      <section className="restaurants-section">
        <div className="restaurants-header">
          <div className="header-content text-center">
            <h2>맛집 리스트</h2>
            <p>숨고 오피스 근처의 맛집을 공유해주세요!</p>
          </div>
          <button 
            className="add-restaurant-btn"
            onClick={() => setIsAddDialogOpen(true)}
          >
            맛집 추가하기
          </button>
        </div>
        <div className="table-container">
          <table className="restaurants-table">
            <thead>
              <tr>
                <th>가게명</th>
                <th>주소</th>
                <th>카테고리</th>
                <th>별점</th>
                <th>거리</th>
                <th>코멘트</th>
                <th>링크</th>
                <th>공유자</th>
                <th>작업</th>
              </tr>
            </thead>
            <tbody>
              {restaurants.map((restaurant) => (
                <tr key={restaurant.id}>
                  <td>{restaurant.name}</td>
                  <td>{restaurant.address}</td>
                  <td>{restaurant.category}</td>
                  <td>{restaurant.rating}</td>
                  <td>{restaurant.distance}</td>
                  <td>{restaurant.comment}</td>
                  <td>
                    {restaurant.link && (
                      <a 
                        href={restaurant.link}
                        target="_blank"
                        rel="noopener noreferrer"
                        className="restaurant-link"
                      >
                        바로가기
                      </a>
                    )}
                  </td>
                  <td>{restaurant.sharedBy}</td>
                  <td className="action-buttons">
                    <button 
                      className="edit-btn"
                      onClick={() => handleEdit(restaurant)}
                    >
                      수정
                    </button>
                    <button 
                      className="delete-btn"
                      onClick={() => deleteRestaurant(restaurant.id)}
                    >
                      삭제
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </section>

      {/* 맛집 추가 다이얼로그 */}
      {isAddDialogOpen && (
        <div className="dialog-overlay" onClick={() => setIsAddDialogOpen(false)}>
          <div className="dialog-content" onClick={e => e.stopPropagation()}>
            <div className="dialog-header">
              <h2>새로운 맛집 추가하기</h2>
              <button 
                className="close-btn"
                onClick={() => setIsAddDialogOpen(false)}
              >
                ✕
              </button>
            </div>
            <div className="dialog-form">
              {[
                { label: '가게명', key: 'name' },
                { label: '주소', key: 'address' },
                { label: '카테고리', key: 'category' },
                { label: '별점', key: 'rating' },
                { label: '거리', key: 'distance' },
                { label: '코멘트', key: 'comment' },
                { label: '링크', key: 'link' },
                { label: '공유자', key: 'sharedBy' }
              ].map((field) => (
                <div key={field.key} className="form-field">
                  <label>{field.label}</label>
                  <input
                    type="text"
                    value={newRestaurant[field.key]}
                    onChange={(e) => setNewRestaurant({
                      ...newRestaurant,
                      [field.key]: e.target.value
                    })}
                  />
                </div>
              ))}
              <button className="save-btn" onClick={addRestaurant}>
                저장하기
              </button>
            </div>
          </div>
        </div>
      )}

      {/* 맛집 수정 다이얼로그 */}
      {isEditDialogOpen && (
        <div className="dialog-overlay" onClick={() => setIsEditDialogOpen(false)}>
          <div className="dialog-content" onClick={e => e.stopPropagation()}>
            <div className="dialog-header">
              <h2>맛집 정보 수정하기</h2>
              <button 
                className="close-btn"
                onClick={() => setIsEditDialogOpen(false)}
              >
                ✕
              </button>
            </div>
            <div className="dialog-form">
              {[
                { label: '가게명', key: 'name' },
                { label: '주소', key: 'address' },
                { label: '카테고리', key: 'category' },
                { label: '별점', key: 'rating' },
                { label: '거리', key: 'distance' },
                { label: '코멘트', key: 'comment' },
                { label: '링크', key: 'link' },
                { label: '공유자', key: 'sharedBy' }
              ].map((field) => (
                <div key={field.key} className="form-field">
                  <label>{field.label}</label>
                  <input
                    type="text"
                    value={editingRestaurant[field.key]}
                    onChange={(e) => setEditingRestaurant({
                      ...editingRestaurant,
                      [field.key]: e.target.value
                    })}
                  />
                </div>
              ))}
              <button className="save-btn" onClick={updateRestaurant}>
                수정 완료
              </button>
            </div>
          </div>
        </div>
      )}

      {/* 카테고리별 맛집 목록 다이얼로그 */}
      {isCategoryDialogOpen && (
        <div className="dialog-overlay" onClick={() => setIsCategoryDialogOpen(false)}>
          <div className="dialog-content category-dialog" onClick={e => e.stopPropagation()}>
            <div className="dialog-header">
              <h2>{selectedCategory} 맛집 목록</h2>
              <button 
                className="close-btn"
                onClick={() => setIsCategoryDialogOpen(false)}
              >
                ✕
              </button>
            </div>
            <div className="category-restaurants">
              <table className="restaurants-table">
                <thead>
                  <tr>
                    <th>가게명</th>
                    <th>주소</th>
                    <th>별점</th>
                    <th>거리</th>
                    <th>코멘트</th>
                  </tr>
                </thead>
                <tbody>
                  {getRestaurantsByCategory(selectedCategory).map((restaurant) => (
                    <tr key={restaurant.id}>
                      <td>{restaurant.name}</td>
                      <td>{restaurant.address}</td>
                      <td>{restaurant.rating}</td>
                      <td>{restaurant.distance}</td>
                      <td>{restaurant.comment}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
